mapper.insert:
INSERT INTO MEMBER_T (
    MEMBER_NO
  , ID
  , NAME
  , GENDER
  , ADDRESS
) VALUES (
    MEMBER_SEQ.NEXTVAL
  , #{id}
  , #{name}
  , #{gender}
  , #{address}
)

mapper.select:
SELECT MEMBER_NO, ID, NAME, GENDER, ADDRESS
  FROM MEMBER_T
 WHERE MEMBER_NO = #{memberNo}

mapper.update:
UPDATE MEMBER_T
   SET NAME = #{name}
     , GENDER = #{gender}
     , ADDRESS = #{address}
 WHERE MEMBER_NO = #{memberNo}
