mapper.select:
SELECT USER_NO, EMAIL, PW, NAME, GENDER, MOBILE, POSTCODE, ROAD_ADDRESS, JIBUN_ADDRESS, DETAIL_ADDRESS, AGREE, PW_MODIFIED_AT, JOINED_AT
  FROM USER_T

  EMAIL = #{email}
  AND PW = #{pw}

mapper.insert:
INSERT INTO ACCESS_T (
    EMAIL
  , LOGIN_AT
) VALUES (
    #{email}
  , SYSDATE
)

mapper.select:
SELECT EMAIL, JOINED_AT, LEAVED_AT
  FROM LEAVE_USER_T
 WHERE EMAIL = #{email}

SELECT EMAIL, PW, NAME, GENDER, MOBILE, POSTCODE, ROAD_ADDRESS, JIBUN_ADDRESS, DETAIL_ADDRESS, AGREE, PW_MODIFIED_AT, JOINED_AT, INACTIVED_AT
  FROM INACTIVE_USER_T
 WHERE EMAIL = #{email}
