mapper.select:
SELECT NO, AUTHOR, TITLE, CONTENT, HIT, IP, POSTDATE
  FROM MVC_BOARD_T
 WHERE NO = #{boardNo}

mapper.insert:
INSERT INTO MVC_BOARD_T (
    NO
  , AUTHOR
  , TITLE
  , CONTENT
  , HIT
  , IP
  , POSTDATE
) VALUES (
    BOARD_SEQ.NEXTVAL
  , #{author}
  , #{title}
  , #{content}
  , #{hit}
  , #{ip}
  , #{postDate}
)
