mapper.update:
UPDATE NOTICE_T
   SET GUBUN = #{gubun}
     , TITLE = #{title}
     , CONTENT = #{content}
 WHERE NOTICE_NO = #{noticeNo}

mapper.select:
SELECT NOTICE_NO, GUBUN, TITLE, CONTENT
  FROM NOTICE_T
 WHERE NOTICE_NO = #{noticeNo}

mapper.insert:
INSERT INTO NOTICE_T ( 
  NOTICE_NO
, GUBUN
, TITLE
, CONTENT
) VALUES (
  NOTICE_SEQ.NEXTVAL
, #{gubun}
, #{title}
, #{content}
)
